//code to search for the carousel and return the value entered by the user
function filter(){
  var FilterValue, input, div, i, items;

  input = document.getElementById("myInput");
  FilterValue = input.value.toUpperCase();
  items = document.getElementById("items");
  div = document.getElementsByClassName('info');

  for (i = 0; i< div.length; i++){
    var a = div[i].getElementsByTagName('div')[0];
    if(a.innerHTML.toLocaleUpperCase().indexOf(FilterValue)> -1){
      div[i].style.display = "";

    } else{
      div[i].style.display = "none";
    }

    

  }


}


//code to make the carousel move from left to right

var span = document.getElementsByTagName('span');
var div = document.getElementsByTagName('div');


var l = 0;
span[1].onclick = ()=>{
  l++;
  for(var i of div)
  {
      if(l== 0){i.style.left = "0px";}
      if(l== 1){i.style.left = "-740px";}
      if(l== 2){i.style.left = "-1480px";}
      if(l== 3){i.style.left = "-2220px";}
      if(l== 4){i.style.left = "-2550px";}
      
     

      if(1>4){l=4;}

  }  
}
